import React, { useRef } from "react";
import axios from "axios";
export default function Form1(props) {
  const filename = useRef();
  const format = useRef();

  console.log();
  async function submitForm(e) {
    e.preventDefault();

    let data = {
      fileName: filename.current.value,
      format: format.current.value,
    };
    console.log(data);
    await axios
      .post(`http://localhost:4000/data?url=${props.url}`, { data })
      .then(function (response) {
        // console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
  }

  return (
    <div>
      <form action="">
        <label for="filename">SaveAs</label>
        <input id="filename" placeholder="<filename>" ref={filename} />
        <label for="format">Format</label>
        <select id="format" ref={format}>
          <option value="mp4">mp4</option>
          <option value="wmv">wmv</option>
          <option value="flv">flv</option>
          <option value="avi">avi</option>
          <option value="mp3">mp3</option>
          <option value="mpg">mpg</option>
        </select>
        <button type="submit" id="download" onClick={submitForm}>
          Send
        </button>
      </form>
    </div>
  );
}
